{
	protected $_resourceDao;
	protected $data;
	
	protected function _getModel()
	{
		if (null === $this->_resourceDao)
		{
			require_once APPLICATION_PATH . '/models/ResourceDao.php';
			$this->_resourceDao = new Model_Resource;
		}
		return $this->_resourceDao;
	}	
	
	public function craftingAction()
	{
		$resourceDao = $this->_getModel();
		$i = 1;
		foreach($this->data as $craftable)
		{
			$dbCraftable = array();
			$dbCraftable['id']=$i;
			$dbCraftable['category'] = "'".$craftable['category']."'";
			$dbCraftable['name'] = "'".$craftable['name']."'";
			$dbCraftable['batch'] = $craftable['batch'];
			$dbCraftable['workload'] = $craftable['workload'];
			$dbCraftable['level'] = $craftable['level'];
			echo 'insert into craftable (id, category, name, batch, workload, level) values (' . implode($dbCraftable, ',') . ');<br />';
			//$craftableDao->insert($dbCraftable);
			//$dbCraftable = $craftableDao->findByName($dbCraftable['name']);
			
			$i++; 
			foreach($craftable['components'] as $component => $amount)
			{
				$formula = array();
				$formula['craftable_id'] = $dbCraftable['id'];
				
				$resource = $resourceDao->findByName($component);
				$formula['resource_id'] = $resource['id'];
				$formula['amount'] = $amount;
				//$formulaDao->insert($formula);
				echo 'insert into formula (craftable_id, resource_id, amount) values (' . implode($formula, ',') . ');<br />';
			}
		}
	}
